import React from 'react'
import PostItem from './PostItem'

const PostList = ({posts}) => {
  return (
	<div>
		<h1 style={{ textAlign : "center" }}>Список постов</h1>
			{
				posts.map(post =>
					<PostItem post={post} key={post.id} />
				)
			}
	</div>
  )
}

export default PostList
